import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { AuthService } from 'src/app/service/auth.service';


@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.css']
})
export class SignupComponent {
  patient: any = {}; // Object to store patient data from the form

  constructor(private http: HttpClient, private authSrv: AuthService) { }

  onSubmit() {
    // Send the patient data to the server for insertion
    this.authSrv.signup(this.patient)
      .subscribe(
        (response) => {
          console.log('Patient added successfully:', response);
          // Reset the form after successful submission
          this.patient = {};
        },
        (error) => {
          console.error('Error adding patient:', error);
          // Handle error response here
        }
      );
  }

}
<div class="container mt-5">
  <form (ngSubmit)="onSubmit()" class="signup-form">
    <h1 class="mb-2 center">Patient SignUp</h1>
    <div class="form-group mb-3">
      <label for="name">Name:</label>
      <input type="text" class="form-control" id="name" placeholder="Enter Your Name" name="Name" [(ngModel)]="patient.Name" required>
    </div>
    <div class="form-group mb-3">
      <label for="gender">Gender:</label>
      <select class="form-control" id="gender" name="Gender" [(ngModel)]="patient.Gender" required>
        <option value="male">Male</option>
        <option value="female">Female</option>
      </select>
    </div>
    <div class="form-group mb-3">
      <label for="dateOfBirth">Date of Birth:</label>
      <input type="date" class="form-control" id="dateOfBirth" name="DateOfBirth" [(ngModel)]="patient.DateOfBirth" required>
    </div>
    <div class="form-group mb-3">
      <label for="contactNumber">Contact Number:</label>
      <input type="text" class="form-control" placeholder="Enter Phone Number" id="contactNumber" name="ContactNumber" [(ngModel)]="patient.ContactNumber" required>
    </div>
    
    <div class="form-group mb-3">
      <label for="email">Email:</label>
      <input type="email" class="form-control" placeholder="Enter Email" id="email" name="Email" [(ngModel)]="patient.Email" required>
    </div>
    <div class="form-group mb-3">
      <label for="password">Password:</label>
      <input type="password" class="form-control" placeholder="Choose Password" id="password" name="Password" [(ngModel)]="patient.Password" required>
    </div>
    <div class="form-group mb-3">
      <label for="address">Address:</label>
      <textarea class="form-control" id="address" placeholder="Enter Address" name="Address" [(ngModel)]="patient.Address" required></textarea>
    </div>
    <div class="form-group mb-3">
      <label for="insuranceInformation">Insurance Information:</label>
      <textarea class="form-control" placeholder="Enter Your Insurance details"  id="insuranceInformation" name="InsuranceInformation" [(ngModel)]="patient.InsuranceInformation"></textarea>
    </div>
    <button type="submit" class="btn btn-primary w-100">Submit</button>
    
    <div class="d-flex justify-content-between mt-4">
      <a class="btn btn-flex btn-outline-secondary no-wrap" routerLink="/login">Patient Login</a>
      <a class="btn btn-flex btn-outline-secondary no-wrap" routerLink="/doctorLogin">Doctor Login</a>
    </div>
    
  </form>
</div>
