import { Component, OnInit } from '@angular/core';
import { NgForm } from '@angular/forms';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-patient-billing',
  templateUrl: './patient-billing.component.html',
  styleUrls: ['./patient-billing.component.css'],
})
export class PatientBillingComponent implements OnInit {
  newBill: any = {};
  bills: any[] = [];

  data:any = {}


  constructor(private authSrv: AuthService) {}

  ngOnInit(): void {
    this.newBill.PatientID = localStorage.getItem('id');
    this.loadData();
  }

  loadData() {
    let id = localStorage.getItem('id');
    this.authSrv.getBillById(id).subscribe((val) => {
      this.bills = val;
      console.log(this.bills);

    });
  }

  submitForm() {
    this.authSrv.createBilling(this.newBill).subscribe(
      (data) => {
        console.log('Bill created successfully:', data);
        this.resetForm();
      },
      (error) => {
        console.error('Error creating bill:', error);
      }
    );
  }
  showPaymentForm(bill: any) {
    bill.showForm = !bill.showForm;
  }

  submitBill() {
    this.authSrv.paybill(this.data).subscribe(
      (data: any) => {
        console.log('Bill created successfully:', data);
        this.resetForm();
      },
      (error) => {
        console.error('Error creating bill:', error);
      }
    );
  }




  resetForm() {
    this.newBill = {};
  }
}
<div class="bill-form-container">
  <h2>Create New Bill</h2>
  <form (ngSubmit)="submitForm()">
    <div class="form-group">
      <label for="patientID">Patient ID:</label>
      <input
        type="text"
        id="patientID"
        name="patientID"
        [(ngModel)]="newBill.PatientID"
        required
        readonly
      />
    </div>
    <div class="form-group">
      <label for="totalAmount">Total Amount:</label>
      <input
        type="number"
        id="totalAmount"
        name="totalAmount"
        [(ngModel)]="newBill.TotalAmount"
        required
      />
    </div>
    <div class="form-group">
      <label for="paymentStatus">Payment Status:</label>
      <select
        id="paymentStatus"
        name="paymentStatus"
        [(ngModel)]="newBill.PaymentStatus"
        required
      >
        <option value="Paid">Paid</option>
        <option value="Pending">Pending</option>
      </select>
    </div>
    <div class="form-group">
      <label for="billDate">Bill Date:</label>
      <input
        type="date"
        id="billDate"
        name="billDate"
        [(ngModel)]="newBill.BillDate"
        required
      />
    </div>
    <button type="submit">Billing</button>
  </form>
</div>

<div class="container">
  <h1>Bill Details</h1>
  <div class="bill-details" *ngFor="let bill of bills">
    <div class="field">
      <span class="label">Bill ID:</span>
      <span class="value">{{ bill.BillID }}</span>
    </div>
    <div class="field">
      <span class="label">Patient ID:</span>
      <span class="value">{{ bill.PatientID }}</span>
    </div>
    <div class="field">
      <span class="label">Total Amount:</span>
      <span class="value">{{ bill.TotalAmount }}</span>
    </div>
    <div class="field">
      <span class="label">Payment Status:</span>
      <span class="value">{{ bill.PaymentStatus }}</span>
    </div>
    <div class="field">
      <span class="label">Bill Date:</span>
      <span class="value">{{ bill.BillDate | date }}</span>
    </div>
    <div class="field">
      <span class="label">Patient Name:</span>
      <span class="value">{{ bill.PatientName }}</span>
    </div>
    <hr />
    <!-- Button to pay if payment status is pending -->
    <button
      *ngIf="bill.PaymentStatus === 'Pending'"
      (click)="showPaymentForm(bill)"
    >
      Pay
    </button>

    <form *ngIf="bill.showForm" (ngSubmit)="submitBill()" #paymentForm="ngForm">

      <label for="billID">Bill ID:</label><br />
      <input
        type="text"
        id="billID"
        name="billID"
        [(ngModel)]="data.BillID"
      /><br /><br />

      <label for="paymentCode">Payment Code:</label><br />
      <input
        type="text"
        id="paymentCode"
        name="paymentCode"
        [(ngModel)]="data.PaymentCode"
      /><br /><br />

      <label for="paymentMethod">Payment Method:</label><br />
      <input
        type="text"
        id="paymentMethod"
        name="paymentMethod"
        [(ngModel)]="data.PaymentMethod"
      /><br /><br />

      <label for="paymentAmount">Payment Amount:</label><br />
      <input
        type="text"
        id="paymentAmount"
        name="paymentAmount"
        [(ngModel)]="data.PaymentAmount"
      /><br /><br />

      <button type="submit">Submit</button>
    </form>
  </div>
</div>
