import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  patient: any = {}; // Object to store patient data from the form

  constructor(private http: HttpClient, private authSrv: AuthService, private router: Router) { }

  onSubmit() {
    // Send the patient data to the server for insertion
    this.authSrv.login(this.patient).subscribe(data => {
      console.log(data);
      localStorage.setItem('token', data.token)
      localStorage.setItem('id', data.patientID)
      this.router.navigate(['/patientHome'])
    })
  }

}


<div class="form-container">
  <form (ngSubmit)="onSubmit()">
    <h1>Patient Login</h1>
    <div class="form-group">
      <label for="email">Email:</label>
      <input type="email" id="email" required placeholder="Enter your email" [(ngModel)]="patient.Email" name="Email">
    </div>
    <div class="form-group">
      <label for="password">Password:</label>
      <input type="password" id="password" required placeholder="Enter your password" [(ngModel)]="patient.Password" name="Password">
    </div>
    <button type="submit" class="btn-login">Login</button>
    <div class="link-container">
      <a class="btn-link" routerLink="/">Patient Signup</a>
      <a class="btn-link" routerLink="/doctorSignup">Doctor Signup</a>
    </div>
  </form>
</div>
